            "sortable":false,
            "value":"serviceName",
			"class": "column_serviceName",
			"width": "250",
			"class_column": "text-xs-left"
          },
          {  
            "text":"Chủ hồ sơ",
            "align":"center",
            "sortable":false,
            "value":"applicantName",
			"class": "column_applicantName",
			"width": "200",
			"class_column": "text-xs-left",
            "layout_view": {
		      template: "<div><p>{{item.applicantName}}</p><span>{{item.wardName}}</span><span v-if=\"item.wardName\">, </span><span>{{item.districtName}}</span></div>",
		      data: () => ({
		        item: this.item
		      })
		    }
          },
          {  
            "text":"Thời gian",
            "align":"center",
            "sortable":false,
            "value":"receiveDate",
			"class": "column_receiveDate",
			"width": "180",
			"class_column": "text-xs-left",
            "layout_view": {
		      template: "<div><p>Tiếp nhận: {{item.receiveDate}}</p><p>Hẹn trả: {{item.dueDate}}</p><p v-if=\"item.dossierOverdue.indexOf(\'Quá\') >= 0\" style=\"color:red\">{{item.dossierOverdue}}</p><p v-else style=\"color:green\">{{item.dossierOverdue}}</p></div>",
		      data: () => ({
		        item: this.item
		      })
		    }
          },
          {  
            "text":"Bước xử lý",
            "align":"center",
            "sortable":false,
            "value":"dueDate",
			"class": "column_dueDate",
			"width": "180",
			"class_column": "text-xs-left",
            "layout_view": {
		      template: "<div><p>{{item.stepName}}</p><p>Quyền: <span v-if=\"item.assigned==1\" style=\"color:blue;font-weight:bold\">Thực hiện</span><span v-else-if=\"item.assigned==2\" style=\"color:blue\">Phối hợp</span><span v-else>Theo dõi</span></p></div>",
		      data: () => ({
		        item: this.item
		      })
		    }
          },
          {  
            "text":"Chuyển đến bởi",
            "align":"center",
            "sortable":false,
            "value":"stepOverdue",
			"class": "column_stepOverdue",
			"width": "120",
			"class_column": "text-xs-left",
            "layout_view": {
		      template: "<div><p>{{item.lastActionUser}}</p><p>{{item.lastActionNote}}</p><p v-if=\"item.stepOverdue.indexOf(\'Quá\') >= 0\" style=\"color:red\">{{item.stepOverdue}}</p><p v-else style=\"color:green\">{{item.stepOverdue}}</p></div>",
		      data: () => ({
		        item: this.item
		      })
		    }
          },
          {  
            "text":"Thao tác",
            "align":"center",
            "sortable":false,
			"class": "column_action",
			"width": "50",
			"class_column": "text-xs-center"
          }         
        ]
      }

