			    vm.item.statusLists = result
			})
			let filter = {
				itemCode: ''
			}
			vm.$store.dispatch('getSubstatusLists', filter).then(function (result) {
			    vm.item.substatusLists = result
			})
			vm.$store.dispatch('getAgencyLists').then(function (result) {
			    vm.item.agencyLists = result
			})			
			vm.$store.dispatch('getServiceLists').then(function (result) {
			    vm.item.serviceLists = result
			})
			vm.$store.dispatch('getDomainLists').then(function (result) {
			    vm.item.domainLists = result
			})			
		  },
		  methods: {
			  getSubStatusListData: function (item) {
				  var vm = this
				  let filter = {
					  itemCode: item.itemCode
				  }
				  vm.$store.dispatch('getSubstatusLists', filter).then(function (result) {
				    vm.item.substatusLists = result
				  })
			  },
			  timKiemCustom: function () {
				  var vm = this
				  let currentQuery = vm.$router.history.current.query
				  vm.$router.push({
			          path: vm.$router.history.current.path,
			          query: {
		                recount: Math.floor(Math.random() * (100 - 1 + 1)) + 1,
		                renew: Math.floor(Math.random() * (100 - 1 + 1)) + 1,
		                agency: vm.item.agency.administrationCode,
		                service: vm.item.service.serviceCode,
		                domain: vm.item.domain.domainCode,
		                status: vm.item.status.itemCode,
		                substatus: vm.item.substatus.itemCode,
		                year: vm.item.year.value,
		                month: vm.item.month.value,
		                top: vm.item.top.value,
		                keyword: vm.item.keyword,
		                register: vm.item.register,
		                q: currentQuery['q']
		              }
			        })
			  }
		  }
        }
      }

